  const metadata = JSON.parse($('Generate Metadata').item.json.choices[0].message.content);
  const title = metadata.title;
  const author = $('Cloud Function Download').item.json.metadata.uploader;
  const sanitizedTitle = title.replace(/[^a-z0-9\s]/gi, '').replace(/\s+/g, ' ').trim().substring(0, 80);
