  return `${sanitizedTitle} - ${capitalizedAuthor}.mp4`;
})() }}

parameters.jsCode:
const cloudFunction = $('Cloud Function Download').item.json;
const geminiAnalysis = cloudFunction.gemini_analysis?.analysis || 'No analysis available';
const transcription = $('Check Status').item.json.text;
const metadata = JSON.parse($('Generate Metadata').item.json.choices[0].message.content);
const googleDrive = $('Upload to Google Drive').item.json;
let acrResult = $('ACRCloud Music Recognition').item.json;

// Parse ACRCloud result - handle wrapped data field
if (acrResult.data && typeof acrResult.data === 'string') {
  acrResult = JSON.parse(acrResult.data);
}

// Minimum confidence threshold (70%)
const MIN_CONFIDENCE = 0.70;

// Helper function to parse a single music match
const parseMatch = (music, matchType) => {
  const score = music.score || 0;
  return {
    title: music.title,
    artist: music.artists?.[0]?.name || 'Unknown',
    album: music.album?.name || null,
    release_date: music.release_date || null,
    confidence: Math.round(score * 100),
    confidence_raw: score,
    match_type: matchType,
    play_offset_ms: music.play_offset_ms || null,
    duration_ms: music.duration_ms || null,
    spotify_id: music.external_metadata?.spotify?.track?.id || null,
    apple_music_id: music.external_metadata?.apple?.track?.id || null,
    deezer_id: music.external_metadata?.deezer?.track?.id || null
  };
};

// Collect ALL matches from both music and humming arrays
let allMatches = [];
let allMatchesFiltered = [];

if (acrResult.status && acrResult.status.code === 0 && acrResult.metadata) {
  // Get all music matches
  const musicMatches = acrResult.metadata.music || [];
  for (const music of musicMatches) {
    const parsed = parseMatch(music, 'music');
    allMatches.push(parsed);
    if (parsed.confidence_raw >= MIN_CONFIDENCE) {
      allMatchesFiltered.push(parsed);
    }
  }

  // Get all humming matches
  const hummingMatches = acrResult.metadata.humming || [];
  for (const music of hummingMatches) {
    const parsed = parseMatch(music, 'humming');
    allMatches.push(parsed);
    if (parsed.confidence_raw >= MIN_CONFIDENCE) {
      allMatchesFiltered.push(parsed);
    }
  }
}

// Sort by confidence (highest first)
allMatches.sort((a, b) => b.confidence_raw - a.confidence_raw);
allMatchesFiltered.sort((a, b) => b.confidence_raw - a.confidence_raw);

// Build recognition status
let recognitionStatus = 'no_match';
if (allMatches.length > 0) {
  if (allMatchesFiltered.length > 0) {
    recognitionStatus = 'matched';
  } else {
    recognitionStatus = 'low_confidence';
  }
}

// Get highest confidence for summary
const highestConfidence = allMatches.length > 0 ? allMatches[0].confidence : null;

// Return in Cloud Function format for compatibility with Bookmark Processor
return {
  json: {
    success: true,
    // Match Cloud Function's nested structure
    video: {
      file_name: googleDrive.name,
      public_url: cloudFunction.video.public_url,
      size_bytes: cloudFunction.video.size_bytes,
      blob_name: cloudFunction.video.blob_name
    },
    audio: cloudFunction.audio || null,
    metadata: {
      title: cloudFunction.metadata.title,
      duration: cloudFunction.metadata.duration,
      uploader: cloudFunction.metadata.uploader,
      video_id: cloudFunction.metadata.video_id,
      source: cloudFunction.metadata.source,
      thumbnail: cloudFunction.metadata.thumbnail
    },
    gemini_analysis: {
      analysis: geminiAnalysis,
      model: cloudFunction.gemini_analysis?.model || 'gemini-2.0-flash'
    },
    transcription: transcription,
    // Additional fields from Video Processor
    generated_metadata: {
      title: metadata.title,
      description: metadata.description,
      tags: metadata.tags
    },
    music: {
      recognized_songs: allMatchesFiltered,
      recognition_status: recognitionStatus,
      total_matches_found: allMatches.length,
      matches_above_threshold: allMatchesFiltered.length,
      highest_confidence: highestConfidence,
      all_matches_raw: allMatches
    },
    google_drive: {
      file_id: googleDrive.id,
      file_name: googleDrive.name,
      file_url: googleDrive.webViewLink || 'N/A'
    },
    processed_at: new Date().toISOString()
  }
};
